import React, { useState, useEffect } from 'react'
import './listItem.scss'
import {
    PlayArrow,
    Add,
    ThumbUpAltOutlined,
    ThumbDownOutlined,
} from "@material-ui/icons";
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function ListItem({ index, item }) {
    const [isHovered, setIsHovered] = useState(false);
    const [movie, setMovie] = useState({});
    // const trailer = "https://player.vimeo.com/external/371433846.sd.mp4?s=236da2f3c0fd273d2c6d9a064f3ae35579b2bbdf&profile_id=139&oauth2_token_id=57447761";

    useEffect(() => {
        const getMovie = async () => {
            try {
                const res = await axios.get(`movies/find/${item}`, {
                    headers: {
                        token: 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxNmFjM2IwNDQ0MGQ4MWU0ODAxNzM1OCIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY0MjEyNzQwMywiZXhwIjoxNjQyNTU5NDAzfQ.xTgLOaZzrbABsma4f89O5aTCooPFgmv2pUaVpD0bq_Y'
                    }
                });
                // console.log(res);
                setMovie(res.data);
            } catch (err) {
                console.log(err);
            }
        }

        getMovie();
    }, [item]);

    return (
        <Link to={{pathname: '/watch', movie: movie}}>

        </Link>

    )
}
