import React, { useState, useEffect } from 'react';
import Featured from '../../components/featured/Featured';
import List from '../../components/list/List';
import Navbar from '../../components/navbar/Navbar';
import './home.scss';
import axios from 'axios';

const Home = ({ type }) => {
    const [lists, setLists] = useState([]);
    const [genre, setGenre] = useState(null);

    useEffect(() => {
        const getRandomLists = async () => {
            try {
                const res = await axios.get(
                    `lists${type ? '?type=' + type : ''}${genre ? '&genre=' + genre : ''}`, {
                    headers: {
                        token: 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxNmFjM2IwNDQ0MGQ4MWU0ODAxNzM1OCIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY0MjEyNzQwMywiZXhwIjoxNjQyNTU5NDAzfQ.xTgLOaZzrbABsma4f89O5aTCooPFgmv2pUaVpD0bq_Y'
                    }
                });
                // console.log(res);
                setLists(res.data);
            } catch (err) {
                console.log(err);
            }
        }

        getRandomLists();
    }, [genre, type]);

    return (
        <div className='home'>
            <Navbar />
            <Featured type={type} />
            {lists.map((list) => (
                <List list={list} />
            ))}
        </div>
    )
}

export default Home
